package ifelse;

public class Positive {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	
	Positive L1 = new Positive();
	L1.Negative(-0.456);
	}

    
     public void Negative(double y) {
	if(y<0){
		
		System.out.println("Negative Number " +y);
	}
	
	else {
		System.out.println("Positive Number " +y);
	}

}}
