package staticvariable;

public class Test4 {
	
	static int val=34;
	static float val1=  4.5f;
	double res = 43;

	char grade = 'a';

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Test4 t = new Test4();

		System.out.println("Print Value int" +val);
		System.out.println("Print Value float" +val1);
		System.out.println("Print Value res" +t.res);
		System.out.println("Print Value grade" +t.grade);
		
	}

}
